mapper.select:
select *
from oomall_goods
where (status=2 or status=3) and is_deleted=0 and goods_category_id=#{categoryId}

mapper.update:
update oomall_goods
set oomall_goods.goods_category_id=0
where oomall_goods.goods_category_id = #{goodsCategoryId}

update oomall_goods
set is_deleted = 1
where id = #{id}

update oomall_goods
set is_deleted=1
where goods_category_id = #{cid}

mapper.select:
select count(status)
from oomall_goods
where status != 0
  and id = #{id}
  and is_deleted = 0;

mapper.update:
update oomall_goods
set volume = volume - #{quantity}
where is_deleted = 0
  and volume - #{quantity} >= 0
  and id = #{id}
